const item = 'Sauce Labs Backpack';
_InventoryPage.default.addToCartByName(item);
_InventoryPage.default.cartBadge().should('have.text', '1');
_InventoryPage.default.goToCart();
cy.url().should('include', '/cart.html');
_CartPage.default.itemNames().should('contain.text', item);
// Back, ensure button toggled to Remove
cy.go('back');
cy.contains('.inventory_item', item).within(() => {
  cy.contains('button', 'Remove').should('be.visible');
});

const item = 'Sauce Labs Backpack';
_InventoryPage.default.addToCartByName(item);
_InventoryPage.default.removeByName(item);
// badge disappears when 0 items
_InventoryPage.default.cartBadge().should('not.exist');
_InventoryPage.default.goToCart();
_CartPage.default.items().should('have.length', 0);
// back on inventory, button returns to Add to cart
cy.go('back');
cy.contains('.inventory_item', item).within(() => {
  cy.contains('button', 'Add to cart').should('be.visible');
});

const items = ['Sauce Labs Backpack', 'Sauce Labs Bike Light', 'Sauce Labs Bolt T-Shirt'];
items.forEach(name => _InventoryPage.default.addToCartByName(name));
_InventoryPage.default.cartBadge().should('have.text', String(items.length));
_InventoryPage.default.goToCart();
_CartPage.default.itemNames().then($names => {
  const texts = [...$names].map(n => n.innerText.trim());
  items.forEach(i => expect(texts).to.include(i));
  expect(texts).to.have.length(items.length);
});
